package package5;

public class IterateChainInString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String str = "Hello Friends";
        for(int i=0; i<str.length(); i++)
        {
        	char s = str.charAt(i);
            System.out.print(s+" ");
        }
        System.out.print(" ");
    
        
	}

}
